#!/usr/bin/env python3
import sys

#Problem 9
dna = ''

lengthdna = len(dna)
ATcount = dna.count('A') + dna.count('T')
ATcontent = ATcount/lengthdna

print(f'The AT content of dna is {ATcontent}')

dna2 = sys.argv[1]
dna2len = len(dna2)
ATcount2 = dna2.count('A') + dna2.count('T')
ATcontent2 = ATcount2/dna2len

print(f'The AT content of the sequence entered on the command line is {ATcontent2}')



